import { plantList } from '../datas/plantList';
import { useState } from 'react'


function Categories({ cart, updateCart }) {
    const categories = plantList.reduce(
        (acc, plant) =>
            acc.includes(plant.category) ? acc : acc.concat(plant.category),
        []
    )

    function filterCat(selectedCat) {
        const plantCat = cart.find((plant) => plant.category === selectedCat)
		if (plantCat) {
			const cartFilteredCurrentPlantCat = cart.filter((plant) => plant.category === selectedCat)
			updateCart([...cartFilteredCurrentPlantCat])
		} else {
			updateCart([cart])
		}
    }

    return (
        <select>
            {categories.map((cat) => (
                <option key={cat} onClick={() => filterCat()}>{cat}</option>
            ))}
        </select>
    )
}

export default Categories;
